  fileDownloadViaSasUrl(selectedRow, ViewIcon) {
    var myRegexp = /(\d+)\D*$/g;
    var match = myRegexp.exec(selectedRow.download_url);
    this.sharedService.startLoading();
    this.map.getDocumentSasUrlByUrl(match[1]).pipe(takeUntil(this.isActive)).subscribe(res => {
      if (res.url) {
        if (ViewIcon) {
          window.open(res.url, '_blank');
        } else {
          this.sharedService.startLoading();
          this.alertService.success("Your report started downloading"); //Create XMLHTTP Request.

          var req = new XMLHttpRequest();
          req.open("GET", res.url, true);
          req.responseType = "blob";

          req.onload = function () {
            //Convert the Byte Data to BLOB object.
            var blob = new Blob([req.response], {
              type: "application/pdf"
            }); //Check the Browser type and download the File.

            var url = window.URL || window.webkitURL;
            var link = url.createObjectURL(blob);
            var a = document.createElement("a");
            a.setAttribute("download", res.filename);
            a.setAttribute("href", link);
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
          };

          req.send();
        }
      }

      this.sharedService.stopLoading();
    }, err => {
      this.alertService.error('Error while downloading report.');
      this.sharedService.stopLoading();
    });
    this.notificationsService.updateNotification();
    selectedRow.is_seen = true;
  }

  downloadexl() {
    this.sharedService.startLoading();
    this.map.downloadReportCSV(); //this.sharedService.stopLoading();
