n.map-form-field[_ngcontent-%COMP%]   .search-field[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\n  background: #222 !important;\n  height: 46px;\n  color: #fff !important;\n  font-size: 14px;\n  border-radius: 10px;\n  padding-right: 40px;\n  border: 0 !important;\n}\n\n.map-form-field[_ngcontent-%COMP%]   .search-icon[_ngcontent-%COMP%] {\n  position: absolute;\n  right: 0;\n  height: 46px;\n  font-size: 22px;\n  padding: 13px 15px;\n  z-index: 9;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   .card[_ngcontent-%COMP%] {\n  background: #222222;\n  border: 0;\n  margin: 20px 0;\n  max-width: 420px;\n  border-radius: 10px;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   .card-header[_ngcontent-%COMP%] {\n  font-size: 16px;\n  font-weight: bold;\n  padding: 15px;\n  line-height: 20px;\n  border-bottom: 1px solid #ddd;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   button.btn.default-btn[_ngcontent-%COMP%] {\n  height: 46px;\n  line-height: 46px;\n  margin-bottom: 10px;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin-bottom: 5px;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   span.cross[_ngcontent-%COMP%] {\n  float: right;\n  color: #CCCCCC;\n  font-size: 20px;\n  cursor: pointer;\n}\n\n.index-report-available-box[_ngcontent-%COMP%]   p.notification-text[_ngcontent-%COMP%] {\n  font-size: 12px;\n  color: #FFABA0;\n  margin-bottom: 10px;\n}\n\n@media screen and (max-width: 767px) {\n  .inner-box[_ngcontent-%COMP%] {\n    margin: 0;\n  }\n\n  h3[_ngcontent-%COMP%] {\n    font-size: 21px;\n    margin: 20px 0 20px;\n  }\n\n  .index-report-available-box[_ngcontent-%COMP%]   .col-md-4[_ngcontent-%COMP%] {\n    display: none;\n  }\n  .index-report-available-box[_ngcontent-%COMP%]   .col-md-8[_ngcontent-%COMP%] {\n    text-align: center;\n  }\n\n  .filter[_ngcontent-%COMP%] {\n    max-width: 120px;\n    margin-bottom: 8px;\n  }\n\n  .view-report[_ngcontent-%COMP%] {\n    display: none;\n  }\n\n  .report-panel[_ngcontent-%COMP%]   td.action-icon[_ngcontent-%COMP%] {\n    text-align: center;\n    min-width: 125px;\n  }\n}\n\n.custom-select-dropdown[_ngcontent-%COMP%]   #customDropdown[_ngcontent-%COMP%] {\n  width: 160px;\n}\n\n.custom-select-dropdown[_ngcontent-%COMP%]   .dropdown-item[_ngcontent-%COMP%] {\n  color: #fff;\n}\n\n.card-body[_ngcontent-%COMP%]   img[_ngcontent-%COMP%] {\n  max-width: 108px;\n  border-radius: 5px;\n}\n\n.report-panel[_ngcontent-%COMP%]   table[_ngcontent-%COMP%]   tr.highlightTR[_ngcontent-%COMP%] {\n  font-weight: bold;\n}\n\n.mapdropcont[_ngcontent-%COMP%] {\n  margin-right: 20px;\n}\n\n.reports-table[_ngcontent-%COMP%]   tr[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:last-child {\n  text-align: center;\n}\n\n.tooltip-box[_ngcontent-%COMP%] {\n  position: absolute;\n  bottom: 40px;\n  background: #222222;\n  right: 40px;\n  width: 280px;\n  border-radius: 10px;\n  padding: 20px 10px;\n  height: 142px;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   .top-list[_ngcontent-%COMP%]   span[_ngcontent-%COMP%] {\n  font-size: 12px;\n  line-height: 14px;\n  color: #aaa;\n  padding-left: 48px;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  font-size: 14px;\n  line-height: 16px;\n  margin: 0 10px 10px;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   p.line-bar[_ngcontent-%COMP%] {\n  margin: 0;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   img.bar-info-icon[_ngcontent-%COMP%] {\n  margin-left: 5px;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   .tooltip-info-hover[_ngcontent-%COMP%] {\n  background: #222;\n  border-radius: 10px;\n  padding: 20px;\n  width: 235px;\n  height: 150px;\n  position: absolute;\n  top: -60%;\n  left: 0;\n  box-shadow: 0 0 4px #111;\n  display: none;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   .tooltip-info-hover[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  font-size: 14px;\n  line-height: 18px;\n  color: #ddd;\n  margin: 0;\n}\n\n.tooltip-box[_ngcontent-%COMP%]   .bar-info-icon[_ngcontent-%COMP%]:hover    + .tooltip-info-hover[_ngc